import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './HistorySession.css';

// 定义会话项类型（后续可扩展更多字段）
interface SessionItem {
  id: number | string;
  name: string;
}

const HistorySession: React.FC = () => {
  const API_BASE_URL = process.env.REACT_APP_API_URL || 'http://localhost:8001';
  // 改用数组存储会话列表（更符合实际业务，便于增删）
  const [sessions, setSessions] = useState<SessionItem[]>([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState<string | null>(null);

  // 组件挂载时获取会话列表
  useEffect(() => {
    const fetchSessions = async () => {
      try {
        setLoading(true);
        // 假设接口返回 { sessions: [{id: 1, name: 'Session 1'}, ...] }
        const response = await axios.get(`${API_BASE_URL}/session/get-user-sessions`);
        
        // 格式化会话数据（适配新的数组结构）
        const sessionList = response.data.sessions || [];
        setSessions(sessionList);
        setError(null);
      } catch (err) {
        console.error('Failed to get sessions:', err);
        if (axios.isAxiosError(err)) {
          setError(`Request failed: ${err.message || 'Network error'}`);
        } else {
          setError('Failed to retrieve historical conversations, please try again later.');
        }
        setSessions([]);
      } finally {
        setLoading(false);
      }
    };
    fetchSessions();
  }, [API_BASE_URL]);

  // 新建会话处理函数
  const handleCreateNewSession = () => {
    // 这里添加新建会话的逻辑（示例：调用后端接口 + 更新本地列表）
    console.log('创建新会话');
    // 示例：模拟创建新会话（实际需替换为真实接口调用）
    // axios.post(`${API_BASE_URL}/session/create`)
    //   .then(res => {
    //     setSessions([res.data.session, ...sessions]); // 新会话置顶
    //   })
    //   .catch(err => console.error('创建会话失败:', err));
    
    // 临时演示：本地添加新会话（实际项目删除此段，用接口逻辑替代）
    const newSession: SessionItem = {
      id: Date.now(), // 临时ID，实际由后端返回
      name: `Session ${sessions.length + 1}`
    };
    setSessions([newSession, ...sessions]); // 新会话放在最前面
  };

  // 删除会话处理函数
  const handleDeleteSession = (sessionId: number | string, e: React.MouseEvent) => {
    // 阻止事件冒泡（避免触发会话项的点击事件）
    e.stopPropagation();
    
    // 确认删除（可选，提升用户体验）
    if (!window.confirm('确定要删除这个会话吗？')) return;

    // 这里添加删除会话的逻辑（示例：调用后端接口 + 更新本地列表）
    console.log('删除会话:', sessionId);
    // 示例：调用删除接口
    // axios.delete(`${API_BASE_URL}/session/${sessionId}`)
    //   .then(() => {
    //     // 本地过滤掉删除的会话
    //     setSessions(sessions.filter(session => session.id !== sessionId));
    //   })
    //   .catch(err => console.error('删除会话失败:', err));

    // 临时演示：本地删除（实际项目删除此段，用接口逻辑替代）
    setSessions(sessions.filter(session => session.id !== sessionId));
  };

  // 渲染单个会话项（含删除按钮）
  const renderSessionItem = (session: SessionItem) => (
    <div 
      key={session.id} 
      className="history-session-item"
      // 可选：添加会话项点击事件（如打开会话详情）
      onClick={() => console.log('打开会话:', session.id)}
    >
      <span className="session-name">{session.name}</span>
      <button 
        className="session-delete-btn"
        onClick={(e) => handleDeleteSession(session.id, e)}
      >
        ×
      </button>
    </div>
  );

  return (
    <div className="history-container">
      <div className="history-title">Historical Records</div>
      
      {loading && <div className="loading">Loading...</div>}
      {error && <div className="error">{error}</div>}
      
      {!loading && !error && (
        <div className="history-session-list">
          {/* 新建会话按钮（尺寸与会话项一致） */}
          <div 
            className="history-session-item new-session-item"
            onClick={handleCreateNewSession}
          >
            <span className="new-session-text">+ New Session</span>
          </div>
          
          {/* 历史会话列表（为空时显示提示） */}
          {sessions.length === 0 ? (
            <div className="no-sessions">No historical conversation records yet</div>
          ) : (
            sessions.map(renderSessionItem)
          )}
        </div>
      )}
    </div>
  );
};

export default HistorySession;
