import React from 'react';
import './UserProfile.css';

interface UserProfileProps {
  onBack?: () => void;                   // 返回回调
  onPasswordChange?: () => void;         // 修改密码回调
  onLogout?: () => void;                 // 注销账号回调
  avatarUrl?: string;                   // 可选头像地址
}

const UserProfile: React.FC<UserProfileProps> = ({
  onBack,
  onPasswordChange,
  onLogout,
  avatarUrl = 'https://picsum.photos/100/100' // 默认头像
}) => {
  return (
    <div className="user-profile-container">
      {/* 返回按钮 */}
      <button className="back-btn" onClick={onBack}>← 返回</button>

      {/* 用户头像区域 */}
      <div className="avatar-wrapper">
        <img src={avatarUrl} alt="用户头像" className="avatar-img" />
      </div>

      {/* 功能按钮区 */}
      <div className="profile-actions">
        <button className="action-btn" onClick={onPasswordChange}>修改密码</button>
        <button className="action-btn logout-btn" onClick={onLogout}>注销账号</button>
      </div>
    </div>
  );
};

export default UserProfile;
