import React, { useState, useEffect } from 'react';
import axios from 'axios'; // 导入axios
import './HistorySession.css';

// 历史会话记录子组件
const HistorySession: React.FC = () => {
  // 地址
  const API_BASE_URL = process.env.REACT_APP_API_URL || 'http://localhost:8001';

  const [sessionCount, setSessionCount] = useState(0);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState<string | null>(null);

  // 新增：新建会话点击事件（仅占位，可后续补充逻辑）
  const handleNewSession = () => {
    console.log('New conversation');
    // 可后续添加新建会话的接口调用逻辑
  };

  // 新增：删除会话点击事件（仅占位，可后续补充逻辑）
  const handleDeleteSession = (index: number, e: React.MouseEvent) => {
    e.stopPropagation(); // 阻止冒泡
    console.log('删除会话:', index);
    // 可后续添加删除会话的接口调用 + 更新sessionCount的逻辑
    // 示例：setSessionCount(sessionCount - 1);
  };

  // 生成对应数量的会话记录框（仅修改内部渲染逻辑，保留原有循环）
  const renderSessionItems = () => {
    const items = [];
    // 新增：添加新建会话项（放在最顶部）
    items.push(
      <div key="new" className="history-session-item new-session" onClick={handleNewSession}>
        + New Session
      </div>
    );
    // 原有循环逻辑不变，仅新增删除按钮
    for (let i = 1; i <= sessionCount; i++) {
      items.push(
        <div key={i} className="history-session-item">
          Session {i}
          {/* 新增：删除按钮 */}
          <span className="delete-btn" onClick={(e) => handleDeleteSession(i, e)}>×</span>
        </div>
      );
    }
    return items;
  };

  return (
    <div className="history-container">
      <div className="history-title">Historical Records</div>
      
      {loading && <div className="loading">Loading...</div>}
      {error && <div className="error">{error}</div>}
      
      {!loading && !error && sessionCount === 0 && (
        <div className="no-sessions">No historical conversation records yet</div>
      )}
      
      {!loading && !error && sessionCount >= 0 && (
        <div className="history-session-list">
          {renderSessionItems()}
        </div>
      )}
    </div>
  );
};

export default HistorySession;
