import React, { useState, ChangeEvent, FormEvent } from 'react';
import './LoginPage.css';
import loginBanner from '../images/login_banner.jpg';

// 严格约束输入的用户名和密码为String类型
interface LoginFormData {
  username: string;
  password: string;
}

const LoginPage: React.FC = () => {
  // 初始化表单状态，绑定TS类型
  const [formData, setFormData] = useState<LoginFormData>({
    username: '',
    password: ''
  });

  // 输入框变化时的处理函数，通用适配用户名/密码框
  const handleInputChange = (e: ChangeEvent<HTMLInputElement>) => {
    const { name, value } = e.target;
    setFormData(prev => ({
      ...prev,
      [name]: value
    }));
  };

  // 登录表单提交处理（可后续对接接口）
  const handleLogin = (e: FormEvent<HTMLFormElement>) => {
    e.preventDefault(); // 阻止表单默认提交
    // 后续可添加接口请求逻辑，比如 axios.post('/api/login', formData)
    console.log('登录表单数据：', formData);
    alert(`用户名：${formData.username}\n密码：${formData.password}\n（实际项目请替换为接口请求）`);
  };

  // 注册链接跳转处理（可后续配置路由）
  const handleRegister = (e: React.MouseEvent<HTMLAnchorElement>) => {
    e.preventDefault();
    console.log('跳转到注册页面');
    alert('跳转到注册页面（实际项目请配置React Router路由）');
  };

  return (
    <div 
      className="login-page"
      style={{ 
        backgroundImage: `url(${loginBanner})`,
        backgroundSize: 'cover',
        backgroundRepeat: 'no-repeat',
        backgroundPosition: 'left center'
      }}> {/* 背景图片 */}

      <div className="login-form-wrapper"> {/* 右侧表单区域 */}
        <div className="login-form-container"> {/* 右侧表单容器 */}

          <h1 className="login-title"> 登 錄 </h1>

          {/* 登录表单 */}
          <form onSubmit={handleLogin} className="login-form">

            {/* 用户名输入框 */}
            <div className="form-item">
              <input
                type="text"
                name="username"
                value={formData.username}
                onChange={handleInputChange}
                placeholder="请输入用户名"
                className="form-input"
                required
              />
            </div>

            {/* 密码输入框 */}
            <div className="form-item">
              <input
                type="password"
                name="password"
                value={formData.password}
                onChange={handleInputChange}
                placeholder="请输入密码"
                className="form-input"
                required
              />
            </div>

            {/* 登录按钮 - 表单为空时禁用 */}
            <button
              type="submit"
              className="login-btn"
              disabled={!formData.username || !formData.password}
            >
              Log In
            </button>
          </form>

          {/* 注册超链接 */}
          <div className="register-link-wrapper">
            <a href="/register" onClick={handleRegister} className="register-link">
              Register immediately
            </a>
          </div>
        </div>
      </div>
    </div>
  );
};

export default LoginPage;
