import React from 'react';
import './UserProfile.css';

const UserProfile: React.FC = () => {
  return (
    <div className="user-profile-page">
      {/* 页面主容器 */}
      <div className="profile-card">
        {/* 顶部：返回按钮 */}
        <div className="profile-header">
          <button className="back-btn" data-testid="back-btn">← 返回</button>
        </div>

        {/* 中间：头像区域（含更换入口） */}
        <div className="avatar-section">
          <div className="avatar-container">
            <img 
              src="https://picsum.photos/120/120" 
              alt="用户头像" 
              className="avatar-img"
            />
            {/* 更换头像按钮（纯UI，后续加点击逻辑） */}
            <div className="avatar-change-btn">更换头像</div>
          </div>
        </div>

        {/* 底部：功能按钮组 */}
        <div className="profile-actions">
          <button className="action-btn" data-testid="change-pwd-btn">修改密码</button>
          <button className="action-btn logout-btn" data-testid="logout-btn">注销账号</button>
        </div>
      </div>
    </div>
  );
};

export default UserProfile;
