import React from 'react';
import sidebarbutton from '../images/sidebar_button.png'; 

interface SidebarProps {
  isSidebarOpen: boolean; 
  toggleSidebar: () => void;
}

// 定义侧边栏组件
const Sidebar: React.FC<SidebarProps> = ({ isSidebarOpen, toggleSidebar }) => {
  return (
    <>
      {/* 侧边栏触发按钮区域 */}
      <div className={`sidebar-logo ${!isSidebarOpen ? 'open' : ''}`}>
        <img 
          src={sidebarbutton} 
          alt="SideBar" 
          className="sidebar-button" 
          onClick={toggleSidebar} 
          style={{}}
        />
      </div>
      {/* 侧边栏主体区域 */}
      <div className={`sidebar-area ${isSidebarOpen ? 'open' : ''}`}>
        <div className="sidebar-sideberheader">
          <div className="sidebar-userprofile">
            <img 
              src="https://picsum.photos/200/200" 
              alt="Profile Picture" 
              className='sidebar-profilepic'
            />
          </div>
          <div className="sidebar-sidebarclose">
            <img 
              src={sidebarbutton} 
              alt="SideBar" 
              className="sidebar-button" 
              onClick={toggleSidebar} 
            />
          </div>
        </div>
        <div className="sidebar-history">
          {/* 这里可以后续添加历史记录相关内容 */}
        </div>
      </div>
    </>
  );
};

// 导出组件，供其他文件使用
export default Sidebar;
