import React, { useState, useRef, useEffect } from 'react';
import { Upload, Send, FileText, X } from 'lucide-react';
import FileUploadModal from './FileUploadModal';
import FileInfoModal from './FileInfoModal';
import './ChatbotInterface.css';

// ç±»å‹å®šä¹‰ï¼ˆæ ¹æ®åŸæœ‰é€»è¾‘è¡¥å……ï¼‰
interface Message {
  id: string;
  type: 'user' | 'bot';
  content: string;
  fileInfo?: {
    name: string;
    size: number;
    type: string;
  };
  metadata?: any;
}

interface ChatState {
  messages: Message[];
  isLoading: boolean;
  currentFile: { name: string; size: number; type: string } | null;
  extractedData: any;
  rawFileContent: string | null;
}

interface BatchProcessingResponse {
  total_files: number;
  successful: number;
  failed: number;
  skipped: number;
  results: Array<{
    status: 'success' | 'error' | 'skipped';
    main_file: string;
    email_file?: string;
    case_id?: string;
    message: string;
    structured_data?: any;
  }>;
}

// å ä½å›¾ç‰‡ï¼ˆå®é™…å¯æ›¿æ¢ä¸ºçœŸå®å›¾ç‰‡URLï¼‰
const universityLogo = 'https://placeholder.pics/svg/48x48/EEEEEE/666666/University%20Logo';
const logoImage = 'https://placeholder.pics/svg/48x48/EEEEEE/666666/SRR%20Logo';
const botIcon = 'https://placeholder.pics/svg/40x40/3B82F6/FFFFFF/BOT';
const userIcon = 'https://placeholder.pics/svg/40x40/FFFFFF/3B82F6/USER';
const sidebarIcon = 'https://placeholder.pics/svg/24x24/8B5CF6/FFFFFF/MENU';

const ChatbotInterface: React.FC = () => {
  // åŸæœ‰çŠ¶æ€
  const [inputMessage, setInputMessage] = useState<string>('');
  const [selectedFiles, setSelectedFiles] = useState<File[]>([]);
  const [isUploadModalOpen, setIsUploadModalOpen] = useState<boolean>(false);
  const [isFileInfoModalOpen, setIsFileInfoModalOpen] = useState<boolean>(false);
  const [summaryResult, setSummaryResult] = useState<any>(null);
  const messagesEndRef = useRef<HTMLDivElement>(null);
  
  // æ–°å¢ä¾§è¾¹æ çŠ¶æ€
  const [isSidebarOpen, setIsSidebarOpen] = useState<boolean>(false);

  // åˆå§‹åŒ–èŠå¤©çŠ¶æ€
  const [chatState, setChatState] = useState<ChatState>({
    messages: [
      {
        id: '1',
        type: 'bot',
        content: 'Welcome to SRR Case Processing Assistant! Upload your files to start processing cases.',
      },
    ],
    isLoading: false,
    currentFile: null,
    extractedData: null,
    rawFileContent: null,
  });

  // æ»šåŠ¨åˆ°åº•éƒ¨
  useEffect(() => {
    messagesEndRef.current?.scrollIntoView({ behavior: 'smooth' });
  }, [chatState.messages]);

  // æ–°å¢ï¼šåˆ‡æ¢ä¾§è¾¹æ æ˜¾ç¤º/éšè—
  const toggleSidebar = () => {
    setIsSidebarOpen(!isSidebarOpen);
  };

  // åŸæœ‰æ–¹æ³•ï¼šæ·»åŠ æ¶ˆæ¯
  const addMessage = (
    type: 'user' | 'bot',
    content: string,
    fileInfo?: any,
    metadata?: any
  ) => {
    setChatState((prev) => ({
      ...prev,
      messages: [
        ...prev.messages,
        {
          id: Date.now().toString(),
          type,
          content,
          fileInfo,
          metadata,
        },
      ],
    }));
  };

  // åŸæœ‰æ–¹æ³•ï¼šå¤„ç†æ–‡ä»¶é€‰æ‹©
  const handleFileSelection = (files: File[]) => {
    setSelectedFiles((prev) => [...prev, ...files]);
    addMessage('user', `Selected ${files.length} new file(s): ${files.map(f => f.name).join(', ')}`);
  };

  // åŸæœ‰æ–¹æ³•ï¼šç§»é™¤å•ä¸ªæ–‡ä»¶
  const handleRemoveFile = (index: number) => {
    const newFiles = [...selectedFiles];
    const removedFile = newFiles.splice(index, 1);
    setSelectedFiles(newFiles);
    addMessage('bot', `Removed file: ${removedFile[0].name}`);
  };

  // åŸæœ‰æ–¹æ³•ï¼šæ¸…ç©ºæ‰€æœ‰æ–‡ä»¶
  const handleClearAllFiles = () => {
    setSelectedFiles([]);
    addMessage('bot', 'All files cleared from selection.');
  };

  // åŸæœ‰æ–¹æ³•ï¼šå¤„ç†æ–‡ä»¶ä¸Šä¼ /å¤„ç†
  const handleFileUpload = async () => {
    setIsUploadModalOpen(false);
    const files = selectedFiles;
    if (files.length === 0) {
      addMessage('bot', 'Please select files to process first.');
      return;
    }

    // Single file processing
    if (files.length === 1) {
      const file = files[0];
      const fileInfo = {
        name: file.name,
        size: file.size,
        type: file.type,
      };

      addMessage('user', `Upload file: ${file.name}`, fileInfo);

      // Provide different processing hints based on file type
      let processingMessage = 'Processing your file, please wait...';
      if (file.name.toLowerCase().startsWith('rcc') && file.type === 'application/pdf') {
        processingMessage = 'Processing RCC PDF file, OCR recognition may take 1-2 minutes, please be patient...';
      } else if (file.type === 'application/pdf') {
        processingMessage = 'Processing PDF file, please wait...';
      }
      addMessage('bot', processingMessage);

      setChatState(prev => ({
        ...prev,
        isLoading: true,
        currentFile: fileInfo,
      }));

      try {
        // æ­¤å¤„ä¸ºåŸæœ‰é€»è¾‘å ä½ï¼Œå®é™…éœ€æ›¿æ¢ä¸ºçœŸå®çš„processFileè°ƒç”¨
        // const result = await processFile(file);
        const result = {
          status: 'success',
          data: {
            A_date_received: '2024-01-01',
            B_source: 'Phone Call',
            C_case_number: 'SRR-2024-001',
            D_type: 'Slope Complaint',
            E_caller_name: 'John Doe',
            F_contact_no: '1234567890',
            G_slope_no: 'S-001',
            H_location: 'Main Street, City',
            I_nature_of_request: 'Slope instability',
            J_subject_matter: 'Slope maintenance',
            K_10day_rule_due_date: '2024-01-11',
            L_icc_interim_due: '2024-01-15',
            M_icc_final_due: '2024-01-20',
          },
          raw_content: 'Raw file content...',
          summary: {
            success: true,
            summary: 'This is a slope complaint case from John Doe regarding slope instability at Main Street.',
            source: file.name
          }
        };
        
        if (result.status === 'success' && result.data) {
          setChatState(prev => ({
            ...prev,
            isLoading: false,
            extractedData: result.data!,
            rawFileContent: result.raw_content,
          }));

          // Display extracted case data in chat
          const caseData = result.data;
          addMessage('bot', `âœ… **File Processing Successful!**

ğŸ“‹ **Extracted Case Information:**

ğŸ“… **Date Received:** ${caseData.A_date_received || 'N/A'}
ğŸ“‹ **Source:** ${caseData.B_source || 'N/A'}
ğŸ”¢ **Case Number:** ${caseData.C_case_number || 'N/A'}
âš¡ **Type:** ${caseData.D_type || 'N/A'}
ğŸ‘¤ **Caller:** ${caseData.E_caller_name || 'N/A'}
ğŸ“ **Contact:** ${caseData.F_contact_no || 'N/A'}
ğŸ—ï¸ **Slope Number:** ${caseData.G_slope_no || 'N/A'}
ğŸ“ **Location:** ${caseData.H_location || 'N/A'}
ğŸ“ **Nature of Request:** ${caseData.I_nature_of_request || 'N/A'}
ğŸ·ï¸ **Subject Matter:** ${caseData.J_subject_matter || 'N/A'}
â° **10-day Due:** ${caseData.K_10day_rule_due_date || 'N/A'}
â° **ICC Interim Due:** ${caseData.L_icc_interim_due || 'N/A'}
â° **ICC Final Due:** ${caseData.M_icc_final_due || 'N/A'}

ğŸ’¬ **You can now ask me questions about this case, such as:**
â€¢ "What is the basic information of this case?"
â€¢ "Contact information"
â€¢ "Slope-related information"
â€¢ "Important dates"
â€¢ "Nature of the case"

ğŸ“Š Click "View Details" to see complete file information and processing status.`);
          

          // display AI summary
        if (result.summary) {
          setSummaryResult(result.summary);
          
          if (result.summary.success && result.summary.summary) {
            addMessage('bot', `ğŸ¤– AI Summary:\n\n"${result.summary.summary}"`, {
              type: 'summary',
              summary: result.summary
            });
          } else {
            addMessage('bot', `âš ï¸ AI summary generation failed: ${result.summary.error || 'Unknown error'}`, {
              type: 'summary-error'
            });
          }
        }

          // Auto-search for similar cases
          try {
            addMessage('bot', 'ğŸ” Searching for similar historical cases...');
            // å ä½é€»è¾‘
            const similarResult = {
              status: 'success',
              total_found: 0
            };
            
            if (similarResult.status === 'success' && similarResult.total_found > 0) {
              // åŸæœ‰é€»è¾‘å ä½
            } else {
              addMessage('bot', 'âœ… No similar historical cases found. This appears to be a unique case.');
            }
          } catch (error) {
            console.error('Similar case search error:', error);
            addMessage('bot', 'âš ï¸ Unable to search similar cases, but file processing was successful.');
          }

          // Clear selected file list
          setSelectedFiles([]);
          setIsUploadModalOpen(false);
        } else {
          setChatState(prev => ({ ...prev, isLoading: false }));
          addMessage('bot', `File processing failed: ${result.message || result.error || 'Unknown error'}`);
          // Clear file list on failure
          setSelectedFiles([]);
          setIsUploadModalOpen(false);
        }
      } catch (error) {
        setChatState(prev => ({ ...prev, isLoading: false }));
        addMessage('bot', `Error processing file: ${error instanceof Error ? error.message : 'Unknown error'}`);
        // Clear file list on error
        setSelectedFiles([]);
        setIsUploadModalOpen(false);
      }
    } 
    // Multi-file batch processing
    else {
      const fileNames = files.map(f => f.name).join(', ');
      addMessage('user', `Batch upload ${files.length} files: ${fileNames}`);
      
      addMessage('bot', `Processing ${files.length} files in batch, please wait...
      ${files.some(f => f.name.toLowerCase().startsWith('rcc')) ? 
      'âš ï¸ RCC files detected, OCR processing may take longer time.' : ''}`);

      setChatState(prev => ({
        ...prev,
        isLoading: true,
        currentFile: { name: `${files.length} files`, size: 0, type: 'batch' },
      }));

      try {
        // å ä½é€»è¾‘
        const result: BatchProcessingResponse = {
          total_files: files.length,
          successful: files.length,
          failed: 0,
          skipped: 0,
          results: files.map(file => ({
            status: 'success' as const,
            main_file: file.name,
            case_id: `SRR-2024-${Math.floor(Math.random() * 1000)}`,
            message: 'Processed successfully',
            structured_data: {
              A_date_received: '2024-01-01',
              C_case_number: `SRR-2024-${Math.floor(Math.random() * 1000)}`,
              H_location: 'Test Location'
            }
          }))
        };
        
        setChatState(prev => ({
          ...prev,
          isLoading: false,
        }));

        // Display batch processing results - æ˜¾ç¤ºæ‰€æœ‰æ–‡ä»¶çš„ç»“æœï¼ŒåŒ…æ‹¬è·³è¿‡çš„
        const successFiles = result.results.filter(r => r.status === 'success');
        const failedFiles = result.results.filter(r => r.status === 'error');
        const skippedFiles = result.results.filter(r => r.status === 'skipped');  // æ–°å¢
        
        let resultMessage = `ğŸ“Š Batch processing completed!

ğŸ“ˆ Processing Statistics:
â€¢ Total files: ${result.total_files}
â€¢ Successfully processed: ${result.successful}
â€¢ Processing failed: ${result.failed}
â€¢ Skipped files: ${result.skipped}`;  // æ·»åŠ è·³è¿‡ç»Ÿè®¡

        // æ˜¾ç¤ºæˆåŠŸæ–‡ä»¶ï¼ˆåŒ…å«è¯¦ç»†ä¿¡æ¯ï¼‰
        if (successFiles.length > 0) {
          resultMessage += `\n\nâœ… Successfully processed files (${successFiles.length}):`;
          successFiles.forEach((f, index) => {
            resultMessage += `\n\n${index + 1}. ${f.main_file}`;
            if (f.email_file) {
              resultMessage += ` (paired with ${f.email_file})`;
            }
            resultMessage += `\n   Case ID: ${f.case_id || 'N/A'}`;
            resultMessage += `\n   Status: ${f.message}`;
            if (f.structured_data) {
              resultMessage += `\n   ğŸ“‹ Extracted: Date=${f.structured_data.A_date_received || 'N/A'}, Case=${f.structured_data.C_case_number || 'N/A'}, Location=${f.structured_data.H_location || 'N/A'}`;
            }
          });
        }

        // æ˜¾ç¤ºå¤±è´¥æ–‡ä»¶
        if (failedFiles.length > 0) {
          resultMessage += `\n\nâŒ Failed to process files (${failedFiles.length}):`;
          failedFiles.forEach((f, index) => {
            resultMessage += `\n${index + 1}. ${f.main_file}`;
            resultMessage += `\n   Case ID: ${f.case_id || 'N/A'}`;
            resultMessage += `\n   Error: ${f.message}`;
          });
        }

        // æ˜¾ç¤ºè·³è¿‡çš„æ–‡ä»¶ï¼ˆæ–°å¢ï¼‰
        if (skippedFiles.length > 0) {
          resultMessage += `\n\nâ­ï¸ Skipped files (${skippedFiles.length}):`;
          skippedFiles.forEach((f, index) => {
            resultMessage += `\n${index + 1}. ${f.main_file}`;
            resultMessage += `\n   Case ID: ${f.case_id || 'N/A'}`;
            resultMessage += `\n   Reason: ${f.message}`;
          });
        }

        if (successFiles.length > 0) {
          resultMessage += `\n\nğŸ’¡ Note: The right information panel shows the last successfully processed file (${successFiles[successFiles.length - 1].main_file}).`;
          resultMessage += `\n   All ${successFiles.length} files have been processed successfully.`;
          resultMessage += `\n   You can ask questions about any of the processed cases.`;
          
          // è®¾ç½®æœ€åä¸€ä¸ªæˆåŠŸæ–‡ä»¶çš„æ•°æ®åˆ°å³ä¾§é¢æ¿
          const lastSuccessFile = successFiles[successFiles.length - 1];
          if (lastSuccessFile.structured_data) {
            setChatState(prev => ({
              ...prev,
              extractedData: lastSuccessFile.structured_data,
            }));
          }
        }

        addMessage('bot', resultMessage);
        
        // Clear file list after batch processing
        setSelectedFiles([]);
        setIsUploadModalOpen(false);
        
      } catch (error) {
        setChatState(prev => ({
          ...prev,
          isLoading: false,
        }));
        
        addMessage('bot', `Error in batch processing files: ${error instanceof Error ? error.message : 'Unknown error'}`);
        
        // Clear file list on error
        setSelectedFiles([]);
        setIsUploadModalOpen(false);
      }
    }
  };

  // åŸæœ‰æ–¹æ³•ï¼šå¤„ç†ç”¨æˆ·æŸ¥è¯¢
  const handleQuery = async () => {
    if (!inputMessage.trim()) return;

    const userQuery = inputMessage.trim();
    setInputMessage('');
    addMessage('user', userQuery);

    setChatState(prev => ({ ...prev, isLoading: true }));

    try {
      // å ä½é€»è¾‘
      const response = `I received your query: "${userQuery}". This is a placeholder response.`;

      setChatState(prev => ({ ...prev, isLoading: false }));
      addMessage('bot', response);
    } catch (error) {
      setChatState(prev => ({ ...prev, isLoading: false }));
      addMessage('bot', `Query failed: ${error instanceof Error ? error.message : 'Unknown error'}`);
    }
  };

  // åŸæœ‰æ–¹æ³•ï¼šé”®ç›˜äº‹ä»¶å¤„ç†
  const handleKeyPress = (e: React.KeyboardEvent) => {
    if (e.key === 'Enter' && !e.shiftKey) {
      e.preventDefault();
      handleQuery();
    }
  };

  return (
    <div className="chatbot-container">
      {/* æ–°å¢ï¼šä¾§è¾¹æ  (ä¸chat-section-fullå¹³è¡Œ) */}
      {isSidebarOpen && (
        <div className="sidebar">
          <div className="sidebar-header">
            <h3>Sidebar Menu</h3>
            <button className="sidebar-close" onClick={toggleSidebar}>
              <X size={20} />
            </button>
          </div>
          <div className="sidebar-content">
            {/* ä¾§è¾¹æ å†…å®¹å ä½ */}
            <p>Sidebar content goes here</p>
            <ul>
              <li>Menu Item 1</li>
              <li>Menu Item 2</li>
              <li>Menu Item 3</li>
            </ul>
          </div>
        </div>
      )}

      {/* åŸæœ‰èŠå¤©åŒºåŸŸ (å®½åº¦è°ƒæ•´ä¸º75%) */}
      <div className="chat-section-full">
        <div className="chat-header">
          <div className="header-content">
            {/* æ–°å¢ï¼šä¾§è¾¹æ è§¦å‘å›¾æ ‡ */}
            <button 
              className="sidebar-toggle-btn" 
              onClick={toggleSidebar}
              title="Toggle Sidebar"
            >
              <img src={sidebarIcon} alt="Menu" className="sidebar-toggle-icon" />
            </button>
            
            <img src={universityLogo} alt="SRR Logo" className="header-logo" />
            <img src={logoImage} alt="SRR Logo" className="header-logo" />
            <div className="header-text">
              <h1>SRR Case Processing Assistant</h1>
              <p>Intelligent File Processing & Case Query System</p>
            </div>
          </div>
        </div>

        <div className="chat-body">
          <div className="chat-inputandoutput">
            <div className="chat-messages">
              {chatState.messages.map((message) => (
                <div key={message.id} className={`message ${message.type}`}>
                  {message.type === 'bot' && (
                    <div className="message-avatar">
                      <img src={botIcon} alt="Bot" className="avatar-image" />
                    </div>
                  )}
                  <div className="message-content">
                    {message.fileInfo && (
                      <div style={{ marginBottom: '8px', fontSize: '12px', opacity: 0.8 }}>
                        <FileText size={14} style={{ display: 'inline', marginRight: '4px' }} />
                        {message.fileInfo.name} ({(message.fileInfo.size / 1024).toFixed(1)} KB)
                      </div>
                    )}

                    <div style={{ whiteSpace: 'pre-line' }}>
                      {message.content}
                    </div>
                  </div>
                  {message.type === 'user' && (
                    <div className="message-avatar">
                      <img src={userIcon} alt="User" className="avatar-image" />
                    </div>
                  )}
                </div>
              ))}
              
              {chatState.isLoading && (
                <div className="message bot">
                  <div className="message-avatar">
                    <img src={botIcon} alt="Bot" className="avatar-image" />
                  </div>
                  <div className="message-content">
                    <div className="loading">
                      <div className="loading-spinner"></div>
                      Processing...
                    </div>
                  </div>
                </div>
              )}
              
              <div ref={messagesEndRef} />
            </div>

            <div className="chat-input">
              <div className="input-container">
                <input
                  type="text"
                  value={inputMessage}
                  onChange={(e) => setInputMessage(e.target.value)}
                  onKeyPress={handleKeyPress}
                  placeholder={
                    chatState.isLoading 
                      ? "Processing..." 
                      : selectedFiles.length > 0 && !chatState.extractedData
                        ? "Click 'Process Files' to start processing or ask questions..."
                        : chatState.extractedData 
                          ? "Ask questions about the case..." 
                          : "Type your message or upload files to get started..."
                  }
                  disabled={chatState.isLoading}
                />
                
                <div className="input-actions">
                  {/* File Upload Button */}
                  <button
                    className="action-button upload-button"
                    onClick={() => setIsUploadModalOpen(true)}
                    disabled={chatState.isLoading}
                    title="Upload files"
                  >
                    <Upload size={18} />
                  </button>

                  {/* Process Files Button */}
                  {selectedFiles.length > 0 && !chatState.extractedData && (
                    <button
                      className="action-button process-button"
                      onClick={handleFileUpload}
                      disabled={chatState.isLoading}
                      title="Process selected files"
                    >
                      {chatState.isLoading ? (
                        <div className="loading-spinner-small"></div>
                      ) : (
                        <>
                          <Upload size={18} />
                          Process
                        </>
                      )}
                    </button>
                  )}

                  {/* View Details Button */}
                  {chatState.extractedData && (
                    <button
                      className="action-button details-button"
                      onClick={() => setIsFileInfoModalOpen(true)}
                      title="View file details"
                    >
                      <FileText size={18} />
                    </button>
                  )}

                  {/* Send Button */}
                  <button
                    className="action-button send-button"
                    onClick={handleQuery}
                    disabled={!inputMessage.trim() || chatState.isLoading}
                    title="Send message"
                    style={{ backgroundColor: '#3b82f6', color: '#fff' }}
                  >
                    <Send size={18} />
                  </button>
                </div>
              </div>
            </div>
          </div>
        </div>
      </div>

      {/* File Upload Modal */}
      <FileUploadModal
        isOpen={isUploadModalOpen}
        onClose={() => setIsUploadModalOpen(false)}
        onFilesSelected={handleFileSelection}
        selectedFiles={selectedFiles}
        onRemoveFile={handleRemoveFile}
        onClearAll={handleClearAllFiles}
        onProcessFiles={handleFileUpload}
        isLoading={chatState.isLoading}
      />

      {/* File Info Modal */}
      <FileInfoModal
        isOpen={isFileInfoModalOpen}
        onClose={() => setIsFileInfoModalOpen(false)}
        fileInfo={chatState.currentFile}
        extractedData={chatState.extractedData}
        summaryResult={summaryResult}
      />
    </div>
  );
};

export default ChatbotInterface;
