import React, { useState, useEffect } from 'react';
import { Menu, FileText, BarChart2, History, Settings, ChevronRight, ChevronLeft } from 'lucide-react';
import './SideBar.css';

// 定义侧边栏菜单项类型
interface SidebarItem {
  id: string;
  title: string;
  icon: React.ReactNode;
  onClick?: () => void;
}

const SideBar: React.FC = () => {
  // 控制侧边栏展开/收起状态
  const [isExpanded, setIsExpanded] = useState(false);
  // 监听鼠标悬停状态
  const [isHovered, setIsHovered] = useState(false);

  // 侧边栏菜单项配置
  const sidebarItems: SidebarItem[] = [
    {
      id: 'dashboard',
      title: 'Dashboard',
      icon: <BarChart2 size={20} />,
      onClick: () => console.log('Dashboard clicked')
    },
    {
      id: 'file-processing',
      title: 'File Processing',
      icon: <FileText size={20} />,
      onClick: () => console.log('File Processing clicked')
    },
    {
      id: 'history',
      title: 'Processing History',
      icon: <History size={20} />,
      onClick: () => console.log('History clicked')
    },
    {
      id: 'settings',
      title: 'Settings',
      icon: <Settings size={20} />,
      onClick: () => console.log('Settings clicked')
    }
  ];

  // 鼠标悬停展开/收起逻辑
  useEffect(() => {
    if (isHovered) {
      setIsExpanded(true);
    } else {
      // 延迟收起，让动画更流畅
      const timer = setTimeout(() => {
        setIsExpanded(false);
      }, 300);
      return () => clearTimeout(timer);
    }
  }, [isHovered]);

  return (
    <div 
      className={`sidebar-container ${isExpanded ? 'expanded' : 'collapsed'}`}
      onMouseEnter={() => setIsHovered(true)}
      onMouseLeave={() => setIsHovered(false)}
    >
      {/* 侧边栏主体 */}
      <div className="sidebar-content">
        {/* 侧边栏头部 */}
        <div className="sidebar-header">
          {isExpanded && (
            <div className="sidebar-logo">
              <Menu size={24} className="logo-icon" />
              <span className="logo-text">SRR Assistant</span>
            </div>
          )}
        </div>

        {/* 侧边栏菜单 */}
        <div className="sidebar-menu">
          {sidebarItems.map((item) => (
            <button
              key={item.id}
              className="sidebar-menu-item"
              onClick={item.onClick}
            >
              <span className="menu-icon">{item.icon}</span>
              {isExpanded && <span className="menu-title">{item.title}</span>}
            </button>
          ))}
        </div>
      </div>

      {/* 展开/收起箭头触发器 */}
      <div className="sidebar-trigger">
        {isExpanded ? (
          <ChevronLeft size={20} />
        ) : (
          <ChevronRight size={20} />
        )}
      </div>
    </div>
  );
};

export default SideBar;
